#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 100005
#define MAXM 30

typedef struct {
    int a, b;
} Item;

int cmp(const void *a, const void *b) {
    Item *x = (Item *)a;
    Item *y = (Item *)b;
    return y->b - x->b;
}

int main() {
    int n, k;
    scanf("%d %d", &n, &k);
    Item items[MAXN];
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &items[i].a, &items[i].b);
    }
    qsort(items, n, sizeof(Item), cmp);
    int dp[1 << MAXM];
    memset(dp, 0, sizeof(dp));
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < MAXM; j++) {
            if ((items[i].a >> j) & 1) {
                dp[k] = (dp[k] > dp[k ^ (1 << j)] + items[i].b) ? dp[k] : dp[k ^ (1 << j)] + items[i].b;
            }
        }
    }
    printf("%d\n", dp[k]);
    return 0;
}
