#include <stdio.h>
#include <string.h>

#define MAX_H 105
#define MAX_W 105

int main() {
    int h, w;
    char str[MAX_H][MAX_W + 1];

    scanf("%d %d", &h, &w);
    for (int i = 0; i < h; i++) {
        scanf("%s", str[i]);
    }

    for (int i = 0; i < h; i++) {
        int cnt[26] = {0};
        for (int j = 0; j < w; j++) {
            cnt[str[i][j] - 'a']++;
        }
        int odd = 0;
        for (int j = 0; j < 26; j++) {
            if (cnt[j] % 2) {
                odd++;
            }
        }
        if (odd > 1) {
            printf("No\n");
            return 0;
        }
    }

    for (int j = 0; j < w; j++) {
        int cnt[26] = {0};
        for (int i = 0; i < h; i++) {
            cnt[str[i][j] - 'a']++;
        }
        int odd = 0;
        for (int i = 0; i < 26; i++) {
            if (cnt[i] % 2) {
                odd++;
            }
        }
        if (odd > 1) {
            printf("No\n");
            return 0;
        }
    }

    printf("Yes\n");
    return 0;
}
