#include <iostream>
#include <vector>
#include <string>
#include <set>
#include <algorithm>

using namespace std;

bool isPalindrome(const string& s) {
    return s == string(s.rbegin(), s.rend());
}

bool check(const vector<string>& A) {
    int H = A.size();
    int W = A[0].size();

    for (int i = 0; i < H; ++i) {
        set<char> row;
        for (int j = 0; j < W; ++j) {
            row.insert(A[i][j]);
        }
        if (row.size() % 2 == 1) return false;
        if (!isPalindrome(string(row.begin(), row.end()))) return false;
    }

    for (int j = 0; j < W; ++j) {
        set<char> col;
        for (int i = 0; i < H; ++i) {
            col.insert(A[i][j]);
        }
        if (col.size() % 2 == 1) return false;
        if (!isPalindrome(string(col.begin(), col.end()))) return false;
    }

    return true;
}

int main() {
    int H, W;
    cin >> H >> W;

    vector<string> A(H);
    for (int i = 0; i < H; ++i) {
        cin >> A[i];
    }

    if (check(A)) {
        cout << "Yes" << endl;
    } else {
        cout << "No" << endl;
    }

    return 0;
}
