import java.util.*;
import java.io.*;

public class Main {
    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        int H = Integer.parseInt(st.nextToken());
        int W = Integer.parseInt(st.nextToken());
        char[][] grid = new char[H][W];
        for (int i = 0; i < H; i++) {
            String row = br.readLine();
            for (int j = 0; j < W; j++) {
                grid[i][j] = row.charAt(j);
            }
        }
        boolean[][] seen = new boolean[H][W];
        boolean[][] visited = new boolean[H][W];
        for (int i = 0; i < H; i++) {
            for (int j = 0; j < W; j++) {
                if (!visited[i][j]) {
                    if (!dfs(grid, seen, visited, i, j)) {
                        System.out.println("No");
                        return;
                    }
                }
            }
        }
        System.out.println("Yes");
    }

    public static boolean dfs(char[][] grid, boolean[][] seen, boolean[][] visited, int i, int j) {
        if (i < 0 || i >= grid.length || j < 0 || j >= grid[0].length) {
            return true;
        }
        if (visited[i][j]) {
            return true;
        }
        if (seen[i][j]) {
            return false;
        }
        seen[i][j] = true;
        if (grid[i][j] != grid[i][j ^ 1]) {
            return false;
        }
        visited[i][j] = true;
        return dfs(grid, seen, visited, i - 1, j) && dfs(grid, seen, visited, i + 1, j) && dfs(grid, seen, visited, i, j - 1) && dfs(grid, seen, visited, i, j + 1);
    }
}
