from collections import Counter

def is_palindrome(s):
    return s == s[::-1]

def check_matrix(h, w, matrix):
    for i in range(h):
        row = ''.join(matrix[i])
        if not is_palindrome(row):
            return False
        counter = Counter(row)
        for char in counter:
            if counter[char] % 2 != 0:
                return False

    for j in range(w):
        col = ''.join([matrix[i][j] for i in range(h)])
        if not is_palindrome(col):
            return False
        counter = Counter(col)
        for char in counter:
            if counter[char] % 2 != 0:
                return False

    return True

h, w = map(int, input().split())
matrix = [list(input()) for _ in range(h)]
print("Yes" if check_matrix(h, w, matrix) else "No")
