#include <stdio.h>
#include <string.h>

#define MAX_H 500
#define MAX_W 500
#define MAX_D (MAX_H + MAX_W - 2)

int main() {
    int H, W, D;
    char grid[MAX_H][MAX_W];

    scanf("%d %d %d", &H, &W, &D);

    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            int color = (i + j) % 4;
            if (i > 0 && j > 0 && (i - 1 + j - 1) % D == 0) {
                color = (color + 1) % 4;
            }
            if (i > 0 && j < W - 1 && (i - 1 + j + 1) % D == 0) {
                color = (color + 2) % 4;
            }
            if (i < H - 1 && j > 0 && (i + 1 + j - 1) % D == 0) {
                color = (color + 3) % 4;
            }
            if (i < H - 1 && j < W - 1 && (i + 1 + j + 1) % D == 0) {
                color = (color + 4) % 4;
            }
            grid[i][j] = "RGBY"[color];
        }
    }

    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            printf("%c", grid[i][j]);
        }
        printf("\n");
    }

    return 0;
}
