#include <iostream>
#include <vector>
#include <string>

using namespace std;

int main() {
    int H, W, d;
    cin >> H >> W >> d;

    vector<string> ans(H);
    for (int i = 0; i < H; i++) {
        ans[i].resize(W, ' ');
    }

    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            int x = i % 4;
            int y = j % 4;
            if ((i - j) % d == 0) {
                x = (x + 1) % 4;
            }
            if ((j - i) % d == 0) {
                y = (y + 1) % 4;
            }
            switch (x) {
                case 0: ans[i][j] = 'R'; break;
                case 1: ans[i][j] = 'Y'; break;
                case 2: ans[i][j] = 'G'; break;
                case 3: ans[i][j] = 'B'; break;
            }
        }
    }

    for (const auto& row : ans) {
        cout << row << endl;
    }

    return 0;
}
