import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int H = scanner.nextInt();
        int W = scanner.nextInt();
        int d = scanner.nextInt();

        for (int i = 0; i < H; i++) {
            for (int j = 0; j < W; j++) {
                int color = (i + j) % 4;
                if (i > 0 && Math.abs(i - (i - 1)) + Math.abs(j - j) == d) {
                    color = (color + 1) % 4;
                }
                if (j > 0 && Math.abs(i - i) + Math.abs(j - (j - 1)) == d) {
                    color = (color + 1) % 4;
                }
                System.out.print(getColor(color));
            }
            System.out.println();
        }
    }

    private static String getColor(int color) {
        switch (color) {
            case 0:
                return "R";
            case 1:
                return "Y";
            case 2:
                return "G";
            case 3:
                return "B";
            default:
                return "";
        }
    }
}
