#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 998244353

typedef struct {
    int x, y;
} point;

int n, m;
char a[100005], b[100005], c[100005], d[100005];
point p[100005][100005];

int dfs(int x, int y) {
    if (x < 0 || x >= n + 2 || y < 0 || y >= m + 2) return 0;
    if (p[x][y].x == -1) return 0;
    if (p[x][y].y == 1) return 1;
    p[x][y].y = 1;
    return dfs(x + 1, y) | dfs(x - 1, y) | dfs(x, y + 1) | dfs(x, y - 1);
}

int main() {
    int i, j, k, ans = 0;
    scanf("%d %d", &n, &m);
    scanf("%s", a);
    scanf("%s", b);
    scanf("%s", c);
    scanf("%s", d);
    for (i = 0; i <= n + 1; i++) {
        for (j = 0; j <= m + 1; j++) {
            p[i][j].x = -1;
            p[i][j].y = 0;
        }
    }
    for (i = 1; i <= n; i++) {
        for (j = 1; j <= m; j++) {
            p[i][j].x = 0;
            if (a[i - 1] == '1') p[i][0].x = 1;
            if (b[i - 1] == '1') p[i][m + 1].x = 1;
            if (c[j - 1] == '1') p[0][j].x = 1;
            if (d[j - 1] == '1') p[n + 1][j].x = 1;
        }
    }
    for (i = 1; i <= n + 1; i++) {
        for (j = 1; j <= m + 1; j++) {
            if (p[i][j].x == 1) {
                if (dfs(i, j)) ans++;
            }
        }
    }
    printf("%d\n", ans);
    return 0;
}
