#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

const int MOD = 998244353;

int main() {
    int n, m;
    cin >> n >> m;

    string a, b, c, d;
    cin >> a >> b >> c >> d;

    vector<int> A(n + 2, 0);
    vector<int> B(n + 2, 0);
    vector<int> C(m + 2, 0);
    vector<int> D(m + 2, 0);

    for (int i = 1; i <= n; i++) {
        A[i] = (a[i - 1] == '1');
        B[i] = (b[i - 1] == '1');
    }

    for (int i = 1; i <= m; i++) {
        C[i] = (c[i - 1] == '1');
        D[i] = (d[i - 1] == '1');
    }

    vector<vector<int>> dp(n + 2, vector<int>(m + 2, 0));
    dp[0][0] = 1;

    for (int i = 0; i <= n + 1; i++) {
        for (int j = 0; j <= m + 1; j++) {
            if (A[i] && j > 0) {
                dp[i][j] = (dp[i][j] + dp[i][j - 1]) % MOD;
            }
            if (B[i] && j < m + 1) {
                dp[i][j] = (dp[i][j] + dp[i][j + 1]) % MOD;
            }
            if (C[j] && i > 0) {
                dp[i][j] = (dp[i][j] + dp[i - 1][j]) % MOD;
            }
            if (D[j] && i < n + 1) {
                dp[i][j] = (dp[i][j] + dp[i + 1][j]) % MOD;
            }
        }
    }

    int ans = 0;
    for (int i = 0; i <= n + 1; i++) {
        for (int j = 0; j <= m + 1; j++) {
            ans = (ans + dp[i][j]) % MOD;
        }
    }

    cout << ans << endl;

    return 0;
}
