import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 998244353;
    static int n, m;
    static int[][] a, b, c, d;
    static int[][][] dp;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        n = Integer.parseInt(st.nextToken());
        m = Integer.parseInt(st.nextToken());
        a = new int[n + 2][m + 2];
        b = new int[n + 2][m + 2];
        c = new int[n + 2][m + 2];
        d = new int[n + 2][m + 2];
        for (int i = 0; i < n; i++) {
            String s = br.readLine();
            for (int j = 0; j < m; j++) {
                a[i + 1][j + 1] = s.charAt(j) - '0';
            }
        }
        for (int i = 0; i < n; i++) {
            String s = br.readLine();
            for (int j = 0; j < m; j++) {
                b[i + 1][j + 1] = s.charAt(j) - '0';
            }
        }
        for (int i = 0; i < m; i++) {
            String s = br.readLine();
            for (int j = 0; j < n; j++) {
                c[j + 1][i + 1] = s.charAt(j) - '0';
            }
        }
        for (int i = 0; i < m; i++) {
            String s = br.readLine();
            for (int j = 0; j < n; j++) {
                d[j + 1][i + 1] = s.charAt(j) - '0';
            }
        }
        dp = new int[n + 2][m + 2][2];
        for (int i = 0; i <= n + 1; i++) {
            for (int j = 0; j <= m + 1; j++) {
                for (int k = 0; k < 2; k++) {
                    dp[i][j][k] = -1;
                }
            }
        }
        System.out.println(solve(0, 0, 0));
    }

    static int solve(int x, int y, int dir) {
        if (x == n + 1 && y == m + 1) {
            return 1;
        }
        if (dp[x][y][dir] != -1) {
            return dp[x][y][dir];
        }
        int ans = 0;
        if (dir == 0) {
            if (y > 1 && a[x][y - 1] == 1) {
                ans = (ans + solve(x, y + 1, 0)) % MOD;
            }
            if (y < m && b[x][y + 1] == 1) {
                ans = (ans + solve(x, y - 1, 1)) % MOD;
            }
        } else {
            if (x > 1 && c[x - 1][y] == 1) {
                ans = (ans + solve(x + 1, y, 0)) % MOD;
            }
            if (x < n && d[x + 1][y] == 1) {
                ans = (ans + solve(x - 1, y, 1)) % MOD;
            }
        }
        return dp[x][y][dir] = ans;
    }
}
