#include <stdlib.h>
#include <string.h>

#define MAX_N 100000
#define MAX_M 100000
#define MAX_T 1000
#define MAX_Q 100000

int main() {
    int N, M, T, Q;
    int a[MAX_N + 1];
    int A[MAX_Q + 1], B[MAX_Q + 1];
    int i, j;

    scanf("%d %d %d", &N, &M, &T);
    for (i = 1; i <= N; i++) {
        scanf("%d", &a[i]);
    }
    scanf("%d", &Q);
    for (i = 1; i <= Q; i++) {
        scanf("%d %d", &A[i], &B[i]);
    }

    for (i = 1; i <= Q; i++) {
        int sum = 0;
        for (j = 1; j <= M && j <= N; j++) {
            sum += a[j];
        }
        int min_sum = (B[i] * T) + A[i];
        if (sum >= min_sum) {
            printf("yes\n");
        } else {
            int max_sum = (T - A[i]) * M * a[1];
            for (j = 2; j <= N && max_sum < min_sum; j++) {
                max_sum += M * a[j];
            }
            if (max_sum >= min_sum) {
                printf("yes\n");
            } else {
                printf("no\n");
            }
        }
    }

    return 0;
}
