import sys
input = sys.stdin.readline

MOD = 998244353

def add(a, b):
    a += b
    if a >= MOD:
        a -= MOD
    return a

def mul(a, b):
    return (a * b) % MOD

def pow(a, b):
    res = 1
    while b:
        if b & 1:
            res = mul(res, a)
        a = mul(a, a)
        b >>= 1
    return res

def solve():
    n, m = map(int, input().split())
    a = input().rstrip()
    b = input().rstrip()
    c = input().rstrip()
    d = input().rstrip()

    def check(x, y):
        if x == 0 or x == n + 1:
            return True
        if y == 0 or y == m + 1:
            return True
        return False

    def dfs(x, y, a, b, c, d):
        if check(x, y):
            return 1
        if a[x] == '1' and dfs(x, y + 1, a, b, c, d):
            return 1
        if b[x] == '1' and dfs(x, y - 1, a, b, c, d):
            return 1
        if c[y] == '1' and dfs(x + 1, y, a, b, c, d):
            return 1
        if d[y] == '1' and dfs(x - 1, y, a, b, c, d):
            return 1
        return 0

    ans = 0
    for i in range(n + 2):
        for j in range(m + 2):
            if dfs(i, j, a, b, c, d):
                ans += 1

    print(ans)

t = 1
while t:
    solve()
    t -= 1
