#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

typedef long long ll;

ll n, a[100005], b[100005], c[100005], d[100005], e[100005], f[100005];

ll calc(ll x) {
    ll res = 0;
    for (ll i = 1; i <= n; i++) {
        if (b[i] == x) res++;
    }
    return res;
}

ll solve(ll x) {
    ll res = 0;
    for (ll i = 1; i <= n; i++) {
        if (b[i] == x) {
            res += c[i];
            c[i] = 0;
        }
    }
    return res;
}

int main() {
    cin >> n;
    for (ll i = 1; i <= n; i++) {
        cin >> a[i];
        b[i] = a[i];
    }
    sort(b + 1, b + n + 1);
    ll sum = 0;
    for (ll i = 1; i <= n; i++) {
        sum += b[i];
    }
    for (ll i = 1; i <= n; i++) {
        c[i] = 1;
    }
    ll ans = 0;
    for (ll i = 1; i <= n; i++) {
        if (b[i] == b[i - 1]) {
            c[i] += c[i - 1];
        }
    }
    for (ll i = 1; i <= n; i++) {
        if (b[i] == b[i - 1]) {
            ans += solve(b[i]);
        }
    }
    ll l = 1, r = sum;
    while (l <= r) {
        ll mid = (l + r) / 2;
        ll cnt = 0;
        for (ll i = 1; i <= n; i++) {
            if (b[i] <= mid) {
                cnt += calc(b[i]);
            }
        }
        if (cnt * 2 <= sum) {
            r = mid - 1;
        } else {
            l = mid + 1;
        }
    }
    cout << ans + (sum - l) / 2 << endl;
    return 0;
}
