#include <stdio.h>
#include <stdlib.h>

#define N 105

int main() {
    int n, k, x[N], dp[N][N], f[N][N];

    scanf("%d %d", &n, &k);
    for (int i = 1; i <= n; i++) {
        scanf("%d", &x[i]);
    }

    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            dp[i][j] = 1e9;
            f[i][j] = 1e9;
        }
    }

    for (int i = 1; i <= n; i++) {
        dp[i][i] = abs(x[i] - i);
        f[i][i] = abs(x[i] - k);
    }

    for (int len = 2; len <= n; len++) {
        for (int l = 1; l + len - 1 <= n; l++) {
            int r = l + len - 1;
            for (int i = l; i <= r; i++) {
                dp[l][r] = dp[l][i - 1] + dp[i + 1][r] + abs(x[i] - i);
                f[l][r] = f[l][i - 1] + f[i + 1][r] + abs(x[i] - k);
            }
            for (int i = l; i <= r; i++) {
                dp[l][r] = dp[l][r] < dp[l][i] + f[i + 1][r] ? dp[l][r] : dp[l][i] + f[i + 1][r];
                f[l][r] = f[l][r] < f[l][i] + dp[i + 1][r] ? f[l][r] : f[l][i] + dp[i + 1][r];
            }
        }
    }

    printf("%d\n", dp[1][n]);

    return 0;
}
