#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int N, M, T;
    cin >> N >> M >> T;

    vector<long long> a(N);
    for (int i = 0; i < N; i++) {
        cin >> a[i];
    }

    int Q;
    cin >> Q;

    for (int q = 0; q < Q; q++) {
        int A, B;
        cin >> A >> B;

        bool found = false;
        for (int i = 0; i <= min(M, N); i++) {
            long long sum = 0;
            for (int j = 0; j < i; j++) {
                sum += a[j];
            }
            for (int j = i; j < N; j++) {
                sum += (a[j] * (M - i));
            }

            if ((sum % T) >= A && (sum / T) >= B) {
                found = true;
                break;
            }
        }

        if (found) {
            cout << "yes" << endl;
        } else {
            cout << "no" << endl;
        }
    }

    return 0;
}
