#include <iostream>
#include <algorithm>
#include <climits>

using namespace std;

int main() {
    int A, B, C, D, E, F;
    cin >> A >> B >> C >> D >> E >> F;

    int ans = 0;
    int sugar = 0;

    for (int a = 1; a <= F / (A + E); a++) {
        for (int b = 1; b <= F / (B + E); b++) {
            int w = min(a * A, b * B);
            int s = min(a * C, b * D);

            if (w + s <= F && (s - E * w / (A + E)) / E * (A + E) + w == F) {
                if (s - E * w / (A + E) > 0) {
                    int density = 100 * s / (w + s);
                    if (density > ans) {
                        ans = density;
                        sugar = s - E * w / (A + E);
                    }
                }
            }
        }
    }

    cout << F << " " << sugar << endl;

    return 0;
}
