#include <stdio.h>
#include <limits.h>

#define N 310

int main() {
    int n, i, j, k, a[N][N], d[N][N], f[N][N];
    long long s;

    scanf("%d", &n);
    for (i = 0; i < n; i++) {
        for (j = 0; j < n; j++) {
            scanf("%d", &a[i][j]);
            if (i == j) a[i][j] = 0;
        }
    }

    for (k = 0; k < n; k++) {
        for (i = 0; i < n; i++) {
            for (j = 0; j < n; j++) {
                d[i][j] = a[i][j];
            }
        }
        for (i = 0; i < n; i++) {
            for (j = 0; j < n; j++) {
                d[i][j] = (d[i][j] < d[i][k] + d[k][j] ? d[i][j] : d[i][k] + d[k][j]);
            }
        }
    }

    for (i = 0; i < n; i++) {
        for (j = 0; j < n; j++) {
            if (d[i][j] != a[i][j]) {
                printf("-1\n");
                return 0;
            }
        }
    }

    for (i = 0; i < n; i++) {
        for (j = 0; j < n; j++) {
            f[i][j] = (i == j ? 0 : INT_MAX);
        }
    }

    for (k = 0; k < n; k++) {
        for (i = 0; i < n; i++) {
            for (j = 0; j < n; j++) {
                f[i][j] = (f[i][j] < f[i][k] + f[k][j] ? f[i][j] : f[i][k] + f[k][j]);
            }
        }
    }

    s = 0;
    for (i = 0; i < n; i++) {
        for (j = 0; j < n; j++) {
            s += f[i][j];
        }
    }

    printf("%lld\n", s);

    return 0;
}
