import java.io.*;
import java.util.*;

public class Main {
    static final int N = 305;
    static long[][] a = new long[N][N];
    static long[][] d = new long[N][N];
    static long ans;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int n = Integer.parseInt(br.readLine());
        StringTokenizer st;
        for (int i = 1; i <= n; i++) {
            st = new StringTokenizer(br.readLine());
            for (int j = 1; j <= n; j++) {
                a[i][j] = Long.parseLong(st.nextToken());
            }
        }
        for (int k = 1; k <= n; k++) {
            for (int i = 1; i <= n; i++) {
                for (int j = 1; j <= n; j++) {
                    d[i][j] = Math.min(d[i][j], d[i][k] + d[k][j]);
                }
            }
            for (int i = 1; i <= n; i++) {
                for (int j = 1; j <= n; j++) {
                    if (d[i][j] != a[i][j]) {
                        System.out.println(-1);
                        return;
                    }
                }
            }
            ans += d[1][k] * 2;
            for (int i = 1; i <= n; i++) {
                d[i][k] = d[k][i] = Long.MAX_VALUE;
            }
        }
        System.out.println(ans);
    }
}
