#include <stdio.h>
#include <stdlib.h>

#define MAXN 1005

int n, p[MAXN], x[MAXN], dp[MAXN][2], sum[MAXN][2];

void dfs(int u, int fa) {
    dp[u][0] = x[u];
    dp[u][1] = 0;
    sum[u][0] = x[u];
    sum[u][1] = 0;
    for (int v = p[u]; v; v = p[u]) {
        dfs(v, u);
        dp[u][0] += dp[v][0];
        dp[u][1] += dp[v][1];
        sum[u][0] += sum[v][0];
        sum[u][1] += sum[v][1];
    }
}

int main() {
    scanf("%d", &n);
    for (int i = 2; i <= n; i++) {
        scanf("%d", &p[i]);
    }
    for (int i = 1; i <= n; i++) {
        scanf("%d", &x[i]);
    }
    dfs(1, 0);
    for (int i = 1; i <= n; i++) {
        if (dp[i][0] != sum[i][0] && dp[i][1] != sum[i][1]) {
            printf("IMPOSSIBLE\n");
            return 0;
        }
    }
    printf("POSSIBLE\n");
    return 0;
}
