#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int N = 1005;

int n, p[N], x[N], f[N][N], g[N][N], h[N][N];
vector<int> e[N];

void dfs(int u, int fa) {
    f[u][0] = g[u][0] = h[u][0] = 1;
    for (int v : e[u]) {
        if (v == fa) continue;
        dfs(v, u);
        for (int i = 0; i <= x[u]; ++i) {
            for (int j = 0; j <= x[u]; ++j) {
                if (i + j > x[u]) break;
                if (f[u][i] && g[v][j]) {
                    f[u][i + j] = 1;
                    g[u][i + j] = 1;
                    h[u][i + j] = h[u][i + j] + h[v][j];
                }
            }
        }
    }
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> n;
    for (int i = 2; i <= n; ++i) {
        cin >> p[i];
        e[p[i]].push_back(i);
    }
    for (int i = 1; i <= n; ++i) {
        cin >> x[i];
    }
    dfs(1, 0);
    if (g[1][x[1]]) {
        cout << "POSSIBLE\n";
    } else {
        cout << "IMPOSSIBLE\n";
    }

    return 0;
}
