#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

const int MOD = 1e9 + 7;

int main() {
    int N;
    cin >> N;

    vector<int> x(2 * N), y(2 * N);
    for (int i = 0; i < 2 * N; i++) {
        cin >> x[i] >> y[i];
    }

    vector<int> cnt(N + 1);
    for (int i = 0; i < 2 * N; i++) {
        cnt[x[i]]++;
        cnt[y[i]]++;
    }

    vector<int> a(N + 1), b(N + 1);
    for (int i = 1; i <= N; i++) {
        a[i] = cnt[i];
        b[i] = cnt[i];
    }

    vector<int> fac(2 * N + 1);
    fac[0] = 1;
    for (int i = 1; i <= 2 * N; i++) {
        fac[i] = (int)((long long)fac[i - 1] * i % MOD);
    }

    int ans = 1;
    for (int i = 1; i <= N; i++) {
        if (a[i] == 0 || b[i] == 0) {
            ans = 0;
            break;
        }

        int c = min(a[i], b[i]);
        ans = (int)((long long)ans * fac[c] % MOD);
        a[i] -= c;
        b[i] -= c;
    }

    int inv = (int)((long long)fac[N] * fac[N] % MOD);
    for (int i = 1; i <= N; i++) {
        inv = (int)((long long)inv * fac[a[i]] % MOD);
        inv = (int)((long long)inv * fac[b[i]] % MOD);
    }

    ans = (int)((long long)ans * inv % MOD);
    cout << ans << endl;

    return 0;
}
