import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 1000000007;
    static final int MAXN = 200005;
    static int[] x = new int[MAXN];
    static int[] y = new int[MAXN];
    static int[] cnt = new int[MAXN];
    static int[] vis = new int[MAXN];
    static int[] fac = new int[MAXN];
    static int[] inv = new int[MAXN];
    static int[] f = new int[MAXN];
    static int[] g = new int[MAXN];
    static int[] h = new int[MAXN];
    static int[] invf = new int[MAXN];
    static int[] invg = new int[MAXN];
    static int[] invh = new int[MAXN];

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        int n = Integer.parseInt(br.readLine());
        StringTokenizer st;
        for (int i = 1; i <= 2 * n; i++) {
            st = new StringTokenizer(br.readLine());
            x[i] = Integer.parseInt(st.nextToken());
            y[i] = Integer.parseInt(st.nextToken());
            cnt[x[i]]++;
        }
        for (int i = 1; i <= n; i++) {
            if (cnt[i] == 0) {
                pw.println(0);
                pw.close();
                System.exit(0);
            }
        }
        fac[0] = 1;
        for (int i = 1; i <= 2 * n; i++) {
            fac[i] = (int) ((long) fac[i - 1] * i % MOD);
        }
        inv[2 * n] = pow(fac[2 * n], MOD - 2);
        for (int i = 2 * n - 1; i >= 1; i--) {
            inv[i] = (int) ((long) inv[i + 1] * (i + 1) % MOD);
        }
        for (int i = 1; i <= n; i++) {
            f[i] = (int) ((long) fac[cnt[i]] * invf[cnt[i] - 1] % MOD);
            g[i] = (int) ((long) fac[cnt[i]] * invg[cnt[i] - 1] % MOD);
            h[i] = (int) ((long) fac[cnt[i]] * invh[cnt[i] - 1] % MOD);
            invf[cnt[i]] = pow(f[i], MOD - 2);
            invg[cnt[i]] = pow(g[i], MOD - 2);
            invh[cnt[i]] = pow(h[i], MOD - 2);
        }
        long ans = 1;
        for (int i = 1; i <= n; i++) {
            if (cnt[i] == 1) {
                ans = (ans * 2) % MOD;
            } else {
                ans = (ans * (f[cnt[i]] - g[cnt[i]] + MOD)) % MOD;
                ans = (ans * (f[cnt[i]] - h[cnt[i]] + MOD)) % MOD;
            }
        }
        pw.println(ans);
        pw.close();
    }

    static long pow(long a, long b) {
        long res = 1;
        while (b > 0) {
            if ((b & 1) == 1) {
                res = (res * a) % MOD;
            }
            a = (a * a) % MOD;
            b >>= 1;
        }
        return res;
    }
}
