#include <iostream>
#include <vector>
#include <queue>
#include <unordered_map>

using namespace std;

int main() {
    int n, m;
    cin >> n >> m;

    vector<vector<int>> graph(n);
    for (int i = 0; i < m; i++) {
        int u, v;
        cin >> u >> v;
        graph[u].push_back(v);
        graph[v].push_back(u);
    }

    vector<int> visited(n, 0);
    vector<int> companion(n, 0);
    vector<int> friend_distance(n, 0);

    for (int i = 0; i < n; i++) {
        if (visited[i] == 0) {
            queue<int> q;
            q.push(i);
            visited[i] = 1;
            int friend_count = 0;
            while (!q.empty()) {
                int u = q.front();
                q.pop();
                for (int v : graph[u]) {
                    if (visited[v] == 0) {
                        visited[v] = 1;
                        q.push(v);
                        friend_count++;
                    }
                }
            }
            friend_distance[i] = friend_count;
        }
    }

    for (int i = 0; i < n; i++) {
        if (friend_distance[i] > 0) {
            for (int j = 0; j < n; j++) {
                if (friend_distance[j] > 0 && graph[i][0] == j) {
                    companion[i] = 1;
                    break;
                }
            }
        }
    }

    for (int i = 0; i < n; i++) {
        if (companion[i] == 1) {
            for (int j = 0; j < n; j++) {
                if (companion[j] == 1 && graph[i][0] == j) {
                    for (int k : graph[j]) {
                        if (friend_distance[k] > 0 && graph[i][0] != k) {
                            companion[i] = 0;
                            break;
                        }
                    }
                }
            }
        }
    }

    int count = 0;
    for (int i = 0; i < n; i++) {
        if (companion[i] == 1) {
            count++;
        }
    }

    cout << count << endl;

    return 0;
}
