#include <iostream>
#include <vector>
#include <algorithm>
#include <limits>

using namespace std;

const int INF = numeric_limits<int>::max();

int main() {
    int N, M, R;
    cin >> N >> M >> R;

    vector<int> r(R);
    for (int i = 0; i < R; i++) {
        cin >> r[i];
    }

    vector<vector<int>> G(N);
    for (int i = 0; i < M; i++) {
        int a, b, c;
        cin >> a >> b >> c;
        a--; b--;
        G[a].push_back({b, c});
        G[b].push_back({a, c});
    }

    vector<int> dp(N, INF);
    dp[0] = 0;

    for (int i = 0; i < R; i++) {
        int v = r[i] - 1;
        vector<int> ndp(N, INF);
        for (int j = 0; j < N; j++) {
            for (auto& e : G[j]) {
                int u = e.first;
                int c = e.second;
                ndp[u] = min(ndp[u], dp[j] + c);
            }
        }
        swap(dp, ndp);
    }

    int ans = INF;
    for (int i = 0; i < N; i++) {
        ans = min(ans, dp[i] + dp[i]);
    }

    cout << ans << endl;

    return 0;
}
