import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.Arrays;
import java.util.StringTokenizer;

public class Main {
    static int N, M, R;
    static int[] r = new int[8];
    static int[][] A = new int[205][205];
    static int[][] C = new int[205][205];
    static int[][] dp = new int[1 << 8][8];
    static int[][] pre = new int[1 << 8][8];

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        N = Integer.parseInt(st.nextToken());
        M = Integer.parseInt(st.nextToken());
        R = Integer.parseInt(st.nextToken());
        st = new StringTokenizer(br.readLine());
        for (int i = 0; i < R; i++) {
            r[i] = Integer.parseInt(st.nextToken()) - 1;
        }
        for (int i = 0; i < M; i++) {
            st = new StringTokenizer(br.readLine());
            int a = Integer.parseInt(st.nextToken()) - 1;
            int b = Integer.parseInt(st.nextToken()) - 1;
            int c = Integer.parseInt(st.nextToken());
            A[a][b] = A[b][a] = c;
            C[a][b] = C[b][a] = c;
        }
        for (int i = 0; i < N; i++) {
            for (int j = 0; j < N; j++) {
                for (int k = 0; k < N; k++) {
                    A[j][k] = Math.min(A[j][k], A[j][i] + A[i][k]);
                }
            }
        }
        for (int i = 0; i < (1 << R); i++) {
            for (int j = 0; j < R; j++) {
                if ((i & (1 << j)) != 0) {
                    for (int k = 0; k < R; k++) {
                        if ((i & (1 << k)) != 0) {
                            dp[i][j] = Math.min(dp[i][j], dp[i ^ (1 << j)][k] + A[r[k]][r[j]]);
                            pre[i][j] = k;
                        }
                    }
                }
            }
        }
        int ans = Integer.MAX_VALUE;
        for (int i = 0; i < R; i++) {
            ans = Math.min(ans, dp[(1 << R) - 1][i] + A[r[i]][r[0]] + A[r[0]][r[i]]);
        }
        System.out.println(ans);
    }
}
