#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 100005

int main() {
    int n, i, j, k, a[MAXN], b[MAXN], c[MAXN], x, ans;
    scanf("%d", &n);
    for (i = 0; i < n; i++) {
        scanf("%d", &a[i]);
        b[a[i]]++;
    }
    for (i = 0; i < MAXN; i++) {
        if (b[i] > 0) {
            c[i] = b[i];
            for (j = i - 1; j >= 0; j--) {
                if (b[j] > 0) {
                    c[i] += b[j];
                    c[j] = b[j];
                }
            }
            for (j = i + 1; j < MAXN; j++) {
                if (b[j] > 0) {
                    c[i] += b[j];
                    c[j] = b[j];
                }
            }
        }
    }
    ans = 0;
    for (i = 0; i < MAXN; i++) {
        if (c[i] > ans) {
            ans = c[i];
        }
    }
    printf("%d\n", ans);
    return 0;
}
