#include <iostream>
#include <map>
#include <algorithm>

int main() {
    int n;
    std::cin >> n;

    std::map<int, int> freq;
    for (int i = 0; i < n; ++i) {
        int x;
        std::cin >> x;
        freq[x]++;
    }

    int maxCount = 0;
    for (const auto& pair : freq) {
        int count = pair.second;
        int minVal = pair.first;
        int maxVal = pair.first;

        for (int i = 0; i < n; ++i) {
            int x;
            std::cin >> x;
            if (x < minVal) {
                minVal = x;
                count += freq[minVal];
                freq.erase(minVal);
            } else if (x > maxVal) {
                maxVal = x;
                count += freq[maxVal];
                freq.erase(maxVal);
            }
        }

        maxCount = std::max(maxCount, count);
    }

    std::cout << maxCount << std::endl;

    return 0;
}
