import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[] p = new int[n + 1];
        int[] pos = new int[n + 1];

        for (int i = 1; i <= n; i++) {
            p[i] = scanner.nextInt();
            pos[p[i]] = i;
        }

        int ans = 0;
        for (int i = 1; i <= n; i++) {
            if (i != pos[i]) {
                int j = i;
                while (j != pos[i]) {
                    ans++;
                    int temp = p[j];
                    p[j] = p[pos[j]];
                    p[pos[j]] = temp;
                    pos[p[j]] = j;
                    pos[p[pos[j]]] = pos[j];
                    j = pos[i];
                }
                ans++;
            }
        }

        System.out.println(ans);
    }
}
