#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_N 100005

int main() {
    int n, i, j, k, a[MAX_N], b[MAX_N], freq[MAX_N];
    scanf("%d", &n);
    for (i = 0; i < n; i++) {
        scanf("%d", &a[i]);
        b[i] = a[i];
    }
    qsort(b, n, sizeof(int), compare);
    for (i = 0; i < n; i++) {
        freq[b[i]]++;
    }
    int max_freq = 0;
    for (i = 0; i < n; i++) {
        int x = a[i];
        int l = 0, r = n - 1;
        while (l <= r) {
            int m = (l + r) / 2;
            if (b[m] <= x) {
                l = m + 1;
            } else {
                r = m - 1;
            }
        }
        int count = freq[x];
        if (l > 0) {
            count += freq[b[l - 1]];
        }
        if (r + 1 < n) {
            count += freq[b[r + 1]];
        }
        max_freq = max_freq > count ? max_freq : count;
        x++;
        while (l <= r) {
            int m = (l + r) / 2;
            if (b[m] < x) {
                l = m + 1;
            } else {
                r = m - 1;
            }
        }
        if (l > 0) {
            count = freq[b[l - 1]];
        } else {
            count = 0;
        }
        if (r + 1 < n) {
            count += freq[b[r + 1]];
        }
        max_freq = max_freq > count ? max_freq : count;
        x--;
        while (l <= r) {
            int m = (l + r) / 2;
            if (b[m] <= x) {
                l = m + 1;
            } else {
                r = m - 1;
            }
        }
        if (l > 0) {
            count = freq[b[l - 1]];
        } else {
            count = 0;
        }
        if (r + 1 < n) {
            count += freq[b[r + 1]];
        }
        max_freq = max_freq > count ? max_freq : count;
    }
    printf("%d\n", max_freq);
    return 0;
}

int compare(const void *a, const void *b) {
    return (*(int *)a - *(int *)b);
}
