from collections import deque

def party(N, M, friendships):
    graph = [[] for _ in range(N)]
    for s, t in friendships:
        graph[s].append(t)
        graph[t].append(s)

    visited = [False] * N
    companions = [False] * N
    queue = deque()

    for i in range(N):
        if not visited[i]:
            queue.append(i)
            visited[i] = True
            companions[i] = True

            while queue:
                node = queue.popleft()
                for neighbor in graph[node]:
                    if not visited[neighbor]:
                        queue.append(neighbor)
                        visited[neighbor] = True
                        companions[neighbor] = companions[node]

    return sum(1 for c in companions if c)

N, M = map(int, input().split())
friendships = []
for _ in range(M):
    s, t = map(int, input().split())
    friendships.append((s, t))

print(party(N, M, friendships))
