#include <stdio.h>
#include <stdlib.h>
#include <math.h>
#include <string.h>

#define MOD 998244353
#define MAXN 205

typedef struct {
    int x, y;
} Point;

int cmp(const void *a, const void *b) {
    Point *p1 = (Point *)a;
    Point *p2 = (Point *)b;
    if (p1->x != p2->x) return p1->x - p2->x;
    return p1->y - p2->y;
}

int orientation(Point p, Point q, Point r) {
    int val = (q->y - p->y) * (r->x - q->x) - (q->x - p->x) * (r->y - q->y);
    if (val == 0) return 0;
    return (val > 0) ? 1 : 2;
}

int isConvex(Point p, Point q, Point r, Point s) {
    int o1 = orientation(p, q, r);
    int o2 = orientation(p, q, s);
    int o3 = orientation(q, r, s);
    return (o1 == 0 && o2 == 0 && o3 == 0) || (o1 != o2 && o2 != o3 && o3 != o1);
}

int isInside(Point p, Point q, Point r, Point s) {
    int o1 = orientation(p, q, r);
    int o2 = orientation(p, q, s);
    int o3 = orientation(q, r, s);
    return (o1 == 0 && o2 == 0 && o3 == 0) || (o1 != o2 && o2 != o3 && o3 != o1);
}

int isConvexHull(Point *p, int n) {
    int l = 0;
    for (int i = 1; i < n; i++) {
        if (p[i].x < p[l].x) l = i;
    }
    int p1 = l, p2 = l;
    for (int i = 0; i < n; i++) {
        while (orientation(p[p1], p[(p1 + 1) % n], p[(p1 + 2) % n]) == 2) p1 = (p1 + 1) % n;
        while (orientation(p[p2], p[(p2 - 1 + n) % n], p[(p2 - 2 + n) % n]) == 2) p2 = (p2 - 1 + n) % n;
        if (isConvex(p[p1], p[(p1 + 1) % n], p[(p1 + 2) % n], p[(p1 + 3) % n])) p1 = (p1 + 1) % n;
        if (isConvex(p[p2], p[(p2 - 1 + n) % n], p[(p2 - 2 + n) % n], p[(p2 - 3 + n) % n])) p2 = (p2 - 1 + n) % n;
        if (p1 == p2) return 0;
    }
    return 1;
}

int main() {
    int n;
    scanf("%d", &n);
    Point p[MAXN];
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &p[i].x, &p[i].y);
    }
    qsort(p, n, sizeof(Point), cmp);
    int ans = 0;
    for (int i = 0; i < n; i++) {
        if (isConvexHull(p, n)) {
            int cnt = 0;
            for (int j = 0; j < n; j++) {
                if (isInside(p[i], p[(i + 1) % n], p[(i + 2) % n], p[j])) cnt++;
            }
            ans = (ans + (1 << (cnt - 2))) % MOD;
        }
        for (int j = i + 1; j < n; j++) {
            if (p[j].x == p[i].x && p[j].y == p[i].y) {
                p[j].x = -1;
                break;
            }
        }
    }
    printf("%d\n", ans);
    return 0;
}
