#include <iostream>
#include <vector>
#include <algorithm>
#include <cmath>

using namespace std;

const int MOD = 998244353;

struct Point {
    int x, y;
};

bool comparePoints(const Point& a, const Point& b) {
    if (a.x == b.x) return a.y < b.y;
    return a.x < b.x;
}

int crossProduct(const Point& a, const Point& b, const Point& c) {
    return (b.x - a.x) * (c.y - a.y) - (b.y - a.y) * (c.x - a.x);
}

int orientation(const Point& a, const Point& b, const Point& c) {
    return crossProduct(a, b, c);
}

int convexHull(vector<Point>& points) {
    int n = points.size();
    if (n < 3) return 0;

    sort(points.begin(), points.end(), comparePoints);

    vector<Point> upperHull, lowerHull;
    for (int i = 0; i < n; i++) {
        while (upperHull.size() >= 2 && orientation(upperHull[upperHull.size() - 2], upperHull[upperHull.size() - 1], points[i]) <= 0) {
            upperHull.pop_back();
        }
        while (lowerHull.size() >= 2 && orientation(lowerHull[lowerHull.size() - 2], lowerHull[lowerHull.size() - 1], points[i]) >= 0) {
            lowerHull.pop_back();
        }
        upperHull.push_back(points[i]);
        lowerHull.push_back(points[i]);
    }

    upperHull.pop_back();
    lowerHull.pop_back();

    int m = upperHull.size() + lowerHull.size();
    vector<int> hull(m);
    for (int i = 0; i < upperHull.size(); i++) {
        hull[i] = upperHull[i].x * upperHull[i].y;
    }
    for (int i = 0; i < lowerHull.size(); i++) {
        hull[upperHull.size() + i] = lowerHull[i].x * lowerHull[i].y;
    }

    int result = 0;
    for (int i = 0; i < m; i++) {
        result = (result + hull[i]) % MOD;
    }

    return result;
}

int main() {
    int n;
    cin >> n;
    vector<Point> points(n);
    for (int i = 0; i < n; i++) {
        cin >> points[i].x >> points[i].y;
    }

    int result = 0;
    for (int i = 0; i < (1 << n); i++) {
        vector<Point> subset;
        for (int j = 0; j < n; j++) {
            if (i & (1 << j)) {
                subset.push_back(points[j]);
            }
        }
        result = (result + convexHull(subset)) % MOD;
    }

    cout << result << endl;

    return 0;
}
