#include <stdio.h>
#include <string.h>

#define MAX_WIDTH 1000
#define MAX_HEIGHT 1000

int main() {
    int aw, ah, bw, bh;
    char a[MAX_HEIGHT][MAX_WIDTH], b[MAX_HEIGHT][MAX_WIDTH];

    scanf("%d %d %d %d", &aw, &ah, &bw, &bh);

    for (int i = 0; i < ah; i++) {
        scanf("%s", a[i]);
    }

    for (int i = 0; i < bh; i++) {
        scanf("%s", b[i]);
    }

    int count = 0;

    for (int x = 0; x <= aw - bw; x++) {
        for (int y = 0; y <= ah - bh; y++) {
            int match = 1;

            for (int i = 0; i < bh; i++) {
                for (int j = 0; j < bw; j++) {
                    if (a[y + i][x + j] != b[i][j] && a[y + i][x + j] != '?') {
                        match = 0;
                        break;
                    }
                }
                if (!match) break;
            }

            if (match) count++;
        }
    }

    printf("%d\n", count);

    return 0;
}
