import sys
input = sys.stdin.readline

mod = 998244353

def cross(p1, p2, p3):
    return (p2[0] - p1[0]) * (p3[1] - p1[1]) - (p2[1] - p1[1]) * (p3[0] - p1[0])

def orientation(p1, p2, p3):
    return cross(p1, p2, p3)

def convex_hull(points):
    n = len(points)
    if n < 3:
        return points

    points.sort(key=lambda x: (x[0], x[1]))
    l = 0
    for i in range(1, n):
        if points[i][0] < points[l][0]:
            l = i

    p = l
    q = (p + 1) % n
    hull = [points[p]]
    while True:
        r = None
        for i in range(n):
            if i == p:
                continue
            if r is None or cross(points[q], points[i], points[p]) > 0:
                r = i
        if r == p:
            break
        hull.append(points[r])
        p = r
        q = (q + 1) % n
    return hull

def solve(points):
    n = len(points)
    hull = convex_hull(points)
    n = len(hull)
    dp = [[0] * (n + 1) for _ in range(n + 1)]
    for i in range(n):
        dp[i][i + 1] = 1
    for length in range(2, n + 1):
        for i in range(n - length + 1):
            j = i + length
            for k in range(i + 1, j):
                dp[i][j] = (dp[i][j] + dp[i][k] * dp[k][j]) % mod
    ans = 0
    for i in range(n):
        ans = (ans + dp[0][i] * (1 << (n - i - 1))) % mod
    return ans

N = int(input())
points = []
for _ in range(N):
    x, y = map(int, input().split())
    points.append((x, y))
ans = solve(points)
print(ans)
