#include <stdio.h>
#include <stdlib.h>

#define MAXN 100005

int main() {
    int X, K, Q;
    int r[MAXN], t[MAXN], a[MAXN];
    int n = 0;

    scanf("%d", &X);
    scanf("%d", &K);
    for (int i = 0; i < K; i++) {
        scanf("%d", &r[i]);
        n++;
    }
    scanf("%d", &Q);
    for (int i = 0; i < Q; i++) {
        scanf("%d %d", &t[i], &a[i]);
        n++;
    }

    int ans[MAXN];
    for (int i = 0; i < Q; i++) {
        int t_i = t[i];
        int a_i = a[i];
        int lower = 0, upper = n - 1;
        while (lower < upper) {
            int mid = (lower + upper) / 2;
            if (r[mid] <= t_i) {
                lower = mid + 1;
            } else {
                upper = mid;
            }
        }
        int index = lower - 1;
        if (index < 0) {
            ans[i] = a_i;
        } else if (index >= n - 1) {
            ans[i] = 0;
        } else {
            int time = r[index] - t_i;
            int sand = a_i;
            if (time < r[index + 1] - r[index]) {
                sand = (r[index + 1] - r[index]) - time;
            }
            ans[i] = sand;
        }
    }

    for (int i = 0; i < Q; i++) {
        printf("%d\n", ans[i]);
    }

    return 0;
}
