#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int X;
    cin >> X;

    int K;
    cin >> K;

    vector<int> R(K);
    for (int i = 0; i < K; i++) {
        cin >> R[i];
    }

    int Q;
    cin >> Q;

    vector<int> T(Q);
    vector<int> A(Q);
    for (int i = 0; i < Q; i++) {
        cin >> T[i] >> A[i];
    }

    vector<int> ans(Q);

    for (int i = 0; i < Q; i++) {
        int t = T[i];
        int a = A[i];

        int idx = lower_bound(R.begin(), R.end(), t) - R.begin();

        if (idx == K) {
            ans[i] = min(a, X);
            continue;
        }

        int r = R[idx];
        int sand = min(a, X);

        int drop = r - (t - r);
        sand -= drop;

        if (idx == 0) {
            ans[i] = sand;
            continue;
        }

        int prev_r = R[idx - 1];
        int prev_drop = prev_r - (r - prev_r);
        sand += prev_drop;

        ans[i] = sand;
    }

    for (int i = 0; i < Q; i++) {
        cout << ans[i] << endl;
    }

    return 0;
}
