import java.util.*;
import java.io.*;

public class Main {
    static class Query {
        int t, a;
        Query(int t, int a) {
            this.t = t;
            this.a = a;
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int X = Integer.parseInt(br.readLine());
        int K = Integer.parseInt(br.readLine());
        String[] r = br.readLine().split(" ");
        int[] R = new int[K];
        for (int i = 0; i < K; i++) {
            R[i] = Integer.parseInt(r[i]);
        }
        int Q = Integer.parseInt(br.readLine());
        Query[] queries = new Query[Q];
        for (int i = 0; i < Q; i++) {
            String[] line = br.readLine().split(" ");
            queries[i] = new Query(Integer.parseInt(line[0]), Integer.parseInt(line[1]));
        }
        solve(X, K, R, Q, queries);
    }

    static void solve(int X, int K, int[] R, int Q, Query[] queries) {
        int[] ans = new int[Q];
        for (int i = 0; i < Q; i++) {
            Query query = queries[i];
            int t = query.t;
            int a = query.a;
            int res = 0;
            if (t <= R[0]) {
                res = Math.min(a, X - (t * (X - a) / (R[0] + 1)));
            } else {
                int j = Arrays.binarySearch(R, t);
                if (j < 0) j = -j - 2;
                int prev = R[j];
                int drop = t - prev;
                res = Math.min(a, X - (drop * (X - a) / (R[j + 1] - R[j] + 1)));
            }
            ans[i] = res;
        }
        for (int i = 0; i < Q; i++) {
            System.out.println(ans[i]);
        }
    }
}
