import sys

def solve():
    X = int(input())
    K = int(input())
    R = list(map(int, input().split()))
    Q = int(input())
    T = []
    A = []
    for _ in range(Q):
        t, a = map(int, input().split())
        T.append(t)
        A.append(a)

    ans = []
    for t, a in zip(T, A):
        if t <= R[0]:
            ans.append(min(a, X - a))
            continue
        for i in range(K):
            if R[i] <= t and t <= R[i + 1]:
                ans.append(X - a)
                break
        else:
            ans.append(X - a)

        for i in range(K):
            if R[i] <= t and t <= R[i + 1]:
                ans[-1] = min(ans[-1], X - (X - a) - (R[i + 1] - t))
                break

    print('\n'.join(map(str, ans)))

if __name__ == '__main__':
    solve()
