#include <stdio.h>

int main() {
    int Q, H, S, D;
    long long N;
    long long min = 1000000000000000000LL;

    scanf("%d %d %d %d %lld", &Q, &H, &S, &D, &N);

    long long temp = N;
    while (temp >= 2) {
        if (temp >= 2 && temp % 2 == 0) {
            long long cost = D + (temp - 2) * Q;
            if (cost < min) {
                min = cost;
            }
            temp -= 2;
        } else if (temp >= 1 && temp % 0.5 == 0) {
            long long cost = H + (temp - 1) * Q;
            if (cost < min) {
                min = cost;
            }
            temp -= 1;
        } else if (temp >= 1 && temp % 1 == 0) {
            long long cost = S + (temp - 1) * Q;
            if (cost < min) {
                min = cost;
            }
            temp -= 1;
        } else {
            long long cost = Q * temp;
            if (cost < min) {
                min = cost;
            }
            temp -= 1;
        }
    }

    printf("%lld\n", min);

    return 0;
}
