#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

int main() {
    int aw, ah, bw, bh;
    cin >> aw >> ah >> bw >> bh;

    vector<string> a(ah);
    for (int i = 0; i < ah; i++) {
        cin >> a[i];
    }

    vector<string> b(bh);
    for (int i = 0; i < bh; i++) {
        cin >> b[i];
    }

    int count = 0;
    for (int i = 0; i <= ah - bh; i++) {
        for (int j = 0; j <= aw - bw; j++) {
            bool match = true;
            for (int k = 0; k < bh; k++) {
                for (int l = 0; l < bw; l++) {
                    if (a[i + k][j + l] != '?' && a[i + k][j + l] != b[k][l]) {
                        match = false;
                        break;
                    }
                }
                if (!match) break;
            }
            if (match) count++;
        }
    }

    cout << count << endl;

    return 0;
}
