import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        String s = scanner.next();
        int n = s.length();
        int[] cnt = new int[26];
        int ans = 1;
        for (int i = 0; i < n; i++) {
            cnt[s.charAt(i) - 'a']++;
        }
        for (int i = 0; i < 26; i++) {
            if (cnt[i] > 1) {
                ans *= cnt[i];
            }
        }
        for (int i = 0; i < n; i++) {
            boolean flag = false;
            for (int j = i + 1; j <= n; j++) {
                if (s.charAt(i) != s.charAt(j - 1)) {
                    break;
                }
                if (s.charAt(j) != s.charAt(i + 1)) {
                    flag = true;
                    break;
                }
                if (j - i == 1) {
                    ans++;
                } else {
                    ans *= 2;
                }
            }
            if (flag) {
                break;
            }
        }
        System.out.println(ans);
    }
}
