import sys
import math
import heapq

class Node:
    def __init__(self, x, y, id):
        self.x = x
        self.y = y
        self.id = id

    def __lt__(self, other):
        return (self.x, self.y) < (other.x, other.y)

class Edge:
    def __init__(self, u, v, w):
        self.u = u
        self.v = v
        self.w = w

    def __lt__(self, other):
        return self.w < other.w

def dist(x1, y1, x2, y2):
    return math.sqrt((x1 - x2) ** 2 + (y1 - y2) ** 2)

def main():
    x1, y1, x2, y2 = map(int, input().split())
    n = int(input())
    fountains = []
    for _ in range(n):
        x, y = map(int, input().split())
        fountains.append(Node(x, y, len(fountains)))

    edges = []
    for i in range(n):
        for j in range(i + 1, n):
            edges.append(Edge(fountains[i], fountains[j], dist(fountains[i].x, fountains[i].y, fountains[j].x, fountains[j].y)))
            edges.append(Edge(fountains[i], fountains[j], dist(fountains[i].x, fountains[i].y, fountains[j].x, fountains[j].y) + 2 * 10))

    edges.append(Edge(Node(x1, y1, -1), Node(x2, y2, -1), dist(x1, y1, x2, y2)))

    edges.sort()

    parent = {}
    rank = {}
    for i in range(n):
        parent[fountains[i].id] = fountains[i].id
        rank[fountains[i].id] = 0

    def find(x):
        if parent[x] != x:
            parent[x] = find(parent[x])
        return parent[x]

    def union(x, y):
        rootx = find(x)
        rooty = find(y)
        if rootx != rooty:
            if rank[rootx] > rank[rooty]:
                parent[rooty] = rootx
            else:
                parent[rootx] = rooty
                if rank[rootx] == rank[rooty]:
                    rank[rooty] += 1

    pq = []
    heapq.heappush(pq, edges[0])
    for i in range(1, len(edges)):
        while pq and pq[0].w > edges[i].w:
            heapq.heappop(pq)
        heapq.heappush(pq, edges[i])
        if edges[i].u.id != edges[i].v.id:
            union(edges[i].u.id, edges[i].v.id)

    ans = float('inf')
    for edge in pq:
        if find(edge.u.id) == find(edge.v.id) and edge.u.id != -1 and edge.v.id != -1:
            ans = min(ans, edge.w)

    print(ans)

if __name__ == "__main__":
    main()
