#include <stdio.h>
#include <string.h>

int main() {
    char A[2005], B[2005];
    int n, ans = 0;

    scanf("%s %s", A, B);
    n = strlen(A);

    if (strcmp(A, B) == 0) {
        printf("0\n");
        return 0;
    }

    for (int i = 0; i < n; i++) {
        if (A[i] != B[i]) {
            ans++;
            break;
        }
    }

    if (ans == 0) {
        printf("-1\n");
        return 0;
    }

    int cnt = 0;
    for (int i = 0; i < n; i++) {
        if (B[i] == '1') {
            cnt++;
        }
    }

    if (cnt == 1) {
        printf("%d\n", ans + n - 1);
        return 0;
    }

    int pos = -1;
    for (int i = 0; i < n; i++) {
        if (A[i] != B[i]) {
            pos = i;
            break;
        }
    }

    int l = 0, r = n - 1;
    while (l < r) {
        if (A[l] != B[l]) {
            l++;
        }
        if (A[r] != B[r]) {
            r--;
        }
        if (l >= r) {
            break;
        }
    }

    if (l == r && A[l] != B[l]) {
        ans += n - l;
    } else {
        ans += n - 1;
    }

    printf("%d\n", ans);
    return 0;
}
