import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        String A = scanner.next();
        String B = scanner.next();

        int n = A.length();
        int ans = 0;

        if (A.equals(B)) {
            System.out.println(0);
            return;
        }

        for (int i = 0; i < n; i++) {
            if (A.charAt(i) != B.charAt(i)) {
                ans++;
                for (int j = 0; j < n; j++) {
                    if (B.charAt(j) == '1') {
                        A = A.substring(0, j) + (A.charAt(j) == '0' ? "1" : "0") + A.substring(j + 1);
                        ans++;
                        break;
                    }
                }
                A = A.substring(n) + A.substring(0, n);
                break;
            }
        }

        if (A.equals(B)) {
            System.out.println(ans);
            return;
        }

        int min = Integer.MAX_VALUE;
        for (int i = 0; i < n; i++) {
            int cnt = 0;
            for (int j = 0; j < n; j++) {
                if (A.charAt((i + j) % n) != B.charAt((i + j) % n)) {
                    cnt++;
                }
            }
            min = Math.min(min, cnt);
        }

        System.out.println(min == Integer.MAX_VALUE ? -1 : min + ans);
    }
}
