#include <iostream>
#include <string>

int main() {
    std::string str;
    std::getline(std::cin, str);

    std::string reversedStr = str;
    int left = 0;
    int right = str.length() - 1;

    while (left < right) {
        std::swap(reversedStr[left], reversedStr[right]);
        left++;
        right--;
    }

    std::cout << reversedStr << std::endl;

    return 0;
}
