import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int aw = scanner.nextInt();
        int ah = scanner.nextInt();
        int bw = scanner.nextInt();
        int bh = scanner.nextInt();
        scanner.nextLine(); // Consume newline left-over

        String[] arows = new String[ah];
        String[] brows = new String[bh];

        for (int i = 0; i < ah; i++) {
            arows[i] = scanner.nextLine();
        }

        for (int i = 0; i < bh; i++) {
            brows[i] = scanner.nextLine();
        }

        int count = 0;
        for (int i = 0; i <= ah - bh; i++) {
            for (int j = 0; j <= aw - bw; j++) {
                boolean match = true;
                for (int k = 0; k < bh; k++) {
                    for (int l = 0; l < bw; l++) {
                        if (arows[i + k].charAt(j + l) != brows[k].charAt(l) && arows[i + k].charAt(j + l) != '?') {
                            match = false;
                            break;
                        }
                    }
                    if (!match) break;
                }
                if (match) count++;
            }
        }

        System.out.println(count);
    }
}
