def solve(A, B):
    n = len(A)
    if A == B:
        return 0
    if n == 1:
        if A == B:
            return 0
        else:
            return -1
    if n == 2:
        if A == B:
            return 0
        else:
            return 1
    A = list(map(int, A))
    B = list(map(int, B))
    diff = [(a, b) for a, b in zip(A, B) if a != b]
    if not diff:
        return -1
    if len(diff) == 1:
        a, b = diff[0]
        if a == 0 and b == 1:
            return 1
        elif a == 1 and b == 0:
            return 1
        else:
            return -1
    if len(diff) == 2:
        a1, b1 = diff[0]
        a2, b2 = diff[1]
        if a1 == 0 and b1 == 1 and a2 == 0 and b2 == 1:
            return 2
        elif a1 == 1 and b1 == 0 and a2 == 1 and b2 == 0:
            return 2
        else:
            return -1
    ans = 0
    for i in range(n):
        if A[i] != B[i]:
            ans += 1
            A[i] = 1 - A[i]
            for j in range(i + 1, n):
                if A[j] != B[j]:
                    A[j] = 1 - A[j]
                    ans += 1
    return ans

A = input()
B = input()
print(solve(A, B))
