#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 998244353

long long power(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

int main() {
    char a[10001], b[10001];
    int n, k, i, j, cnt, ans;
    scanf("%s %s", a, b);
    n = strlen(a);
    for (i = 0; i < n; i++) {
        if (a[i] == '1') k++;
    }
    if (k == 0) {
        printf("0\n");
        return 0;
    }
    long long fac[n + 1], inv[n + 1];
    fac[0] = 1;
    for (i = 1; i <= n; i++) fac[i] = fac[i - 1] * i % MOD;
    inv[n] = power(fac[n], MOD - 2);
    for (i = n - 1; i >= 0; i--) inv[i] = inv[i + 1] * (i + 1) % MOD;
    long long C[n + 1][n + 1];
    for (i = 0; i <= n; i++) {
        C[i][0] = 1;
        for (j = 1; j <= i; j++) {
            C[i][j] = (C[i - 1][j - 1] + C[i - 1][j]) % MOD;
        }
    }
    long long dp[n + 1][n + 1];
    for (i = 0; i <= n; i++) {
        for (j = 0; j <= n; j++) {
            dp[i][j] = 0;
        }
    }
    dp[0][0] = 1;
    for (i = 1; i <= n; i++) {
        for (j = 0; j <= i; j++) {
            for (cnt = 0; cnt <= j; cnt++) {
                if (a[i - 1] == '1') {
                    dp[i][j] = (dp[i][j] + dp[i - 1][j - cnt] * C[j][cnt] % MOD * fac[cnt] % MOD * fac[j - cnt] % MOD * inv[k - cnt] % MOD * inv[j - cnt]) % MOD;
                }
            }
        }
    }
    ans = 0;
    for (i = 0; i <= n; i++) {
        ans = (ans + dp[n][i] * fac[i] % MOD * fac[k - i] % MOD * fac[k] % MOD * fac[k] % MOD * power(2, k) % MOD) % MOD;
    }
    printf("%lld\n", ans);
    return 0;
}
