#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

const int mod = 998244353;

int main() {
    string a, b;
    cin >> a >> b;

    int n = a.size();
    vector<int> pos_a, pos_b;
    for (int i = 0; i < n; ++i) {
        if (a[i] == '1') {
            pos_a.push_back(i);
        }
        if (b[i] == '1') {
            pos_b.push_back(i);
        }
    }

    int k = pos_a.size();
    vector<int> inv_a(k), inv_b(k);
    for (int i = 0; i < k; ++i) {
        inv_a[pos_a[i]] = i;
        inv_b[pos_b[i]] = i;
    }

    vector<int> p_a(k), p_b(k);
    iota(p_a.begin(), p_a.end(), 0);
    iota(p_b.begin(), p_b.end(), 0);

    long long ans = 0;
    for (int mask = 0; mask < (1 << k); ++mask) {
        int cnt = 0;
        for (int i = 0; i < k; ++i) {
            if ((mask >> i) & 1) {
                cnt++;
                swap(p_a[i], p_a[cnt - 1]);
                swap(p_b[i], p_b[cnt - 1]);
            }
        }

        bool ok = true;
        for (int i = 0; i < k; ++i) {
            if (a[pos_a[i]] != b[pos_b[i]]) {
                ok = false;
                break;
            }
        }

        if (ok) {
            long long x = 1;
            for (int i = 0; i < k; ++i) {
                x = x * (i + 1) % mod;
            }
            ans = (ans + x) % mod;
        }
    }

    ans = ans * pow(k * 2, 2) % mod;
    cout << ans << endl;

    return 0;
}
